mapper.select:
SELECT hotel_order.id, hotel_order.order_no, hotel_order.room_number, hotel_order.mobile, hotel_order.check_in_date, hotel_order.check_out_date, hotel_order.created_time, hotel_order.`status`, room.`name` AS roomName, hotel_order.shop_id, hotel_order.room_id, shop.`name` AS shopName, GROUP_CONCAT(hotel_order_user.realname) AS realname FROM shop, hotel_order LEFT JOIN room ON hotel_order.room_id = room.id LEFT JOIN hotel_order_user ON hotel_order.id = hotel_order_user.order_id WHERE hotel_order.is_deleted = 0 AND hotel_order.shop_id = shop.id
AND hotel_order_user.realname = #{queryDTO.realname}
AND hotel_order.room_id = #{queryDTO.roomId}
AND room.`name` like CONCAT('%',#{queryDTO.roomName},'%')
AND hotel_order.shop_id = #{queryDTO.shopId}
AND shop.`name` like CONCAT('%',#{queryDTO.shopName},'%')
AND hotel_order.order_no = #{queryDTO.orderNo}
AND hotel_order.check_in_date = #{queryDTO.checkInDate}
AND hotel_order.created_time >= #{queryDTO.createdTimeStart}
AND hotel_order.created_time <= #{queryDTO.createdTimeEnd}
AND 1 = 1
AND hotel_order.`status` = #{queryDTO.status}
GROUP BY hotel_order.id ORDER BY find_in_set(hotel_order.`status`,'2,3,1,4,6,7,5')
